import { useState } from "react";
import { HexColorPicker } from "react-colorful";
import "./colorPicker.css";
import Dropdown from 'react-dropdown';
import "react-dropdown/style.css";

const colorList = [
  {
    id: 1,
    name: "white",
    hex: "#ffffff",
  },
  {
    id: 2,
    name: "black",
    hex: "#000000",
  },
  {
    id: 3,
    name: "red",
    hex: "#ff0000",
  },
  {
    id: 4,
    name: "blue",
    hex: "#00ff00",
  },
  {
    id: 5,
    name: "green",
    hex: "#0000ff",
  },
  {
    id: 6
  }
];

const options = [
    { value : "#ffffff", label : "White" },
    { value : "#00000", label : "Black" },
    { value : "#ff0000", label : "Red" },
    { value : "#00ff00", label : "Blue" },
    { value : "#0000ff", label : "Green" },
]

function Colorpicker({ color, setColor }) {
    const [selected, setSelected] = useState(false);

    function handleSelect(e){
        console.log(e);
    }

  return (
    <div className="colorPicker-wrapper">
      <HexColorPicker color={color} onChange={setColor} />
      <select className="colorList">
        {/* <option value="notSelected" {selected==false && "selected"}></option> */}
        {/* {!selected && <option value="notSelected" selected>Select Color</option>}  */}
        {colorList.map((val) => (
          <option key={val.id} value={val.name}>
            {val.name}
          </option>
        ))}
      </select>
      <Dropdown options={options} onChange={handleSelect}/>
    </div>
  );
}

export default Colorpicker;
