import { useState } from "react";
import { HexColorPicker } from "react-colorful";
import "./colorPicker.css";

const colorList = [
  {
    id: 1,
    name: "white",
    hex: "#ffffff",
  },
  {
    id: 2,
    name: "black",
    hex: "#000000",
  },
  {
    id: 3,
    name: "red",
    hex: "#ff0000",
  },
  {
    id: 4,
    name: "blue",
    hex: "#00ff00",
  },
  {
    id: 5,
    name: "green",
    hex: "#0000ff",
  },
  {
    id: 6
  }
];

function Colorpicker({ color, setColor }) {
    const [selected, setSelected] = useState(false);

  return (
    <div className="colorPicker-wrapper">
      <HexColorPicker color={color} onChange={setColor} />
      <select className="colorList">
        {/* <option value="notSelected" {selected==false && "selected"}></option> */}
        {!selected && <option value="notSelected" selected>Select Color</option>} 
        {colorList.map((val) => (
          <option key={val.id} value={val.name}>
            {val.name}
          </option>
        ))}
      </select>
    </div>
  );
}

export default Colorpicker;
