import "./canvas.css";
import {useState} from 'react';
import {REZIABLE_HELPERS_SIDE_HEIGHT, REZIABLE_HELPERS_SIDE_WIDTH, REZIABLE_HELPERS_CORNER_HEIGHT, REZIABLE_HELPERS_CORNER_WIDTH} from '../../constants/constants';

const Canvas = () => {
    const [height, setHeight] = useState(200);
    const [width, setWidth] = useState(200);
    const [top, setTop] = useState(20);
    const [left, setLeft] = useState(20);

    const canvasStyles = {
        position : 'absolute',
        height : `${height}px`,
        width : `${width}px`,
        top : `${top}px`,
        left : `${left}px`,
        backgroundColor : 'cyan'
    }

    const canvasLeftStyles = {
        position : 'absolute',
        height : `${height}px`,
        width : REZIABLE_HELPERS_SIDE_WIDTH,
        top : `${top}px`,
        left : `${left}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }
    const canvasRightStyles = {
        position : 'absolute',
        height : `${height}px`,
        width : REZIABLE_HELPERS_SIDE_WIDTH,
        top : `${top}px`,
        left : `${left+width}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }
    const canvasTopStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_SIDE_WIDTH,
        width : `${width}px`,
        top : `${top}px`,
        left : `${left}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }
    const canvasBottomStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_SIDE_WIDTH,
        width : `${width}px`,
        top : `${top+height}px`,
        left : `${left}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }

    const canvasLeftTopStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_CORNER_HEIGHT,
        width : REZIABLE_HELPERS_CORNER_WIDTH,
        top : `${top}px`,
        left : `${left}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }

    const canvasRightTopStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_CORNER_HEIGHT,
        width : REZIABLE_HELPERS_CORNER_WIDTH,
        top : `${top}px`,
        left : `${left+width}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }
    const canvasLeftBottomStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_CORNER_HEIGHT,
        width : REZIABLE_HELPERS_CORNER_WIDTH,
        top : `${top+height}px`,
        left : `${left}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }
    const canvasRightBottomStyles = {
        position : 'absolute',
        height : REZIABLE_HELPERS_CORNER_HEIGHT,
        width : REZIABLE_HELPERS_CORNER_WIDTH,
        top : `${top+height}px`,
        left : `${left+width}px`,
        backgroundColor : 'black',
        zIndex : '2'
    }

    const handleRightResize = (e) => {
        console.log(e.clientX);
    }

    const handleTracker = (e) => {
        console.log(e.clientX);
    }

    const trackXY = () => {
        console.log("Tracking....");
        document.addEventListener("mousemove", handleTracker);
    }

    const cleanUpTracker = () => {
        document.removeEventListener("mousemove", handleTracker);
    }

  return (
    //   <div height={`${height}px`} width={`${width}px`} top={`${top}px`} left={`${left}px`} backgroundColor="cyan" zIndex="10" className="canvas"></div>
    <div className="canvas-container">
        <div style={canvasStyles}></div>
        <div style={canvasLeftStyles} className="cursor-pointer canvas-left"></div>
        <div style={canvasRightStyles} onMouseDown={trackXY} onMouseUp={(e)=>document.removeEventListener('mousemove', handleRightResize)} className="cursor-pointer canvas-right"></div>
        <div style={canvasTopStyles} className="cursor-pointer canvas-top"></div>
        <div style={canvasBottomStyles} className="cursor-pointer canvas-bottom"></div>
        <div style={canvasLeftTopStyles} className="cursor-pointer canvas-left-top"></div>
        <div style={canvasRightTopStyles} className="cursor-pointer canvas-right-top"></div>
        <div style={canvasLeftBottomStyles} className="cursor-pointer canvas-left-bottom"></div>
        <div style={canvasRightBottomStyles} className="cursor-pointer canvas-right-bottom"></div>
    </div>
  );
};

export default Canvas;
